package v1alpha2

type TlsExtSupportModelType string

const (
	TlsExtSupportModelServiceProvider TlsExtSupportModelType = "SERVICE-PROVIDER"
	TlsExtSupportModelUE              TlsExtSupportModelType = "UE"
)

// TlsExtension
//
//	Purpose:
//	  Defines fields required to create TlsExtensionHeader Value to be sent in FAR for https enrichment
//
//	Data model:
//	 Refer to the description for each attribute below
//
//	Usage:
//	  Configured under StaticPcc as a map and referenced by TlsExtensionRef under PccRule
type TlsExtension struct {
	// Tls Extension ID
	// Mandatory
	TlsExtensionID uint16 `mapstructure:"tlsExtensionId" json:"tlsExtensionId"`
	// Identifier for this tlsExtension.
	// This field will not be exposed to the user, will be generated by the map key when controller reconciles map.
	// Optional
	TlsExtensionKey string `mapstructure:"tlsExtensionKey" json:"tlsExtensionKey,omitempty"`
	// Bearer Field Mask
	// apn, imei, imsi, msisdn, nai, ue_ip, upf_ip, uli, timestamp, rat
	// rat-type, qos, mdn are deprecated, use rat, msisdn instead
	// Optional
	BearerFieldMask BearerFieldMaskType `mapstructure:"bearerFieldMask" json:"bearerFieldMask,omitempty"`
	// Policy Field Mask
	// "resolution" ...
	// Optional
	PolicyFieldMask PolicyFieldMaskType `mapstructure:"policyFieldMask" json:"policyFieldMask,omitempty"`
	// StaticField for X-Header
	// "refresh=300" or any meaningful action string
	// Optional
	StaticField string `mapstructure:"staticField"     json:"staticField,omitempty"`
	// DynamicContext for X-Header
	// nonce , pduSessId and/or unique
	// Optional
	DynamicContext string `mapstructure:"dynamicContext"  json:"dynamicContext,omitempty"`
	// Tls Extension supportted models
	// SERVICE-PROVIDER (default)/UE
	// Optional
	TlsExtSupportModel TlsExtSupportModelType `mapstructure:"tlsExtSupportModel" json:"tlsExtSupportModel,omitempty"`
	// Enable Validation
	// Optional
	EnableValidation bool `mapstructure:"enableValidation" json:"enableValidation,omitempty"`
	// Validation Action for application provider
	// Optional
	ValidationAction ValidationActionType `mapstructure:"validationAction" json:"validationAction,omitempty"`
}
